// WICHTIG: Route-Bestimmung basierend auf Query-Parameter (n8n Best Practice!)
// Best Practices: Robust, sauber, eindeutige Logik gemäß n8n Dokumentation
// n8n Webhook Path: hero-blocks (relativ zum Webhook-Base /webhook/)
// Vollständiger Path: /webhook/hero-blocks?checkType=license oder /webhook/hero-blocks?checkType=update
// Gemäß n8n Docs: Query-Parameter werden in query zurückgegeben

let checkType = null;

// Step 1: Versuche Query-Parameter checkType (PRIMÄR - n8n Best Practice!)
// n8n gibt Query-Parameter in $input.item.json.query zurück
const queryParams = $input.item.json.query || {};
if (queryParams.checkType) {
  checkType = String(queryParams.checkType).toLowerCase().trim();
}

// Step 2: Extrahiere aus webhookUrl (Fallback - falls Query-Parameter nicht verfügbar)
// URL-Format: https://n8n.chooomedia.com/webhook/license/hero-blocks
// oder: https://n8n.chooomedia.com/webhook/update/hero-blocks
// ODER: https://n8n.chooomedia.com/webhook/hero-blocks?checkType=license
if (!checkType && $input.item.json.webhookUrl) {
  const url = String($input.item.json.webhookUrl);
  // Matche: /webhook/license/hero-blocks oder /webhook/update/hero-blocks (altes Format)
  const urlMatch = url.match(/\/webhook\/(license|update)\/hero-blocks/i);
  if (urlMatch && urlMatch[1]) {
    checkType = urlMatch[1].toLowerCase().trim(); // license oder update
  } else {
    // Versuche Query-Parameter aus URL
    const urlObj = new URL(url);
    const checkTypeParam = urlObj.searchParams.get('checkType');
    if (checkTypeParam) {
      checkType = checkTypeParam.toLowerCase().trim();
    }
  }
}

// Step 3: Fallback: Versuche aus Request-URL zu extrahieren
if (!checkType && $input.item.json.url) {
  const url = String($input.item.json.url);
  // Matche: /webhook/license/hero-blocks oder /webhook/update/hero-blocks
  const urlMatch = url.match(/\/(license|update)\//i);
  if (urlMatch && urlMatch[1]) {
    checkType = urlMatch[1].toLowerCase().trim();
  } else {
    // Versuche Query-Parameter aus URL
    try {
      const urlObj = new URL(url);
      const checkTypeParam = urlObj.searchParams.get('checkType');
      if (checkTypeParam) {
        checkType = checkTypeParam.toLowerCase().trim();
      }
    } catch (e) {
      // URL parsing fehlgeschlagen - ignoriere
    }
  }
}

// Step 4: Default: license (für Backwards Compatibility)
if (!checkType || (checkType !== 'license' && checkType !== 'update')) {
  checkType = 'license';
}

// Return mit checkType und allen Query-Parametern
return {
  json: {
    ...$input.item.json,
    checkType: checkType,
    // Query-Parameter explizit weitergeben (Best Practice)
    plugin: queryParams.plugin || $input.item.json.plugin || 'hero-blocks',
    version: queryParams.version || $input.item.json.version || '1.0.0',
    shopwareVersion: queryParams.shopwareVersion || $input.item.json.shopwareVersion || '6.7.0',
    timestamp: queryParams.timestamp || queryParams.checkDate || $input.item.json.timestamp || new Date().toISOString(),
    currentVersion: queryParams.currentVersion || queryParams.version || "1.0.0"
  }
};

// WICHTIG: Verarbeite GitHub Releases API Response
// Best Practices: Robust, sauber, eindeutige Logik mit Error-Handling
// Gemäß GitHub API Dokumentation: Releases API gibt tag_name, assets, body zurück

// 1. Lese GitHub API Response
const githubResponse = $input.item.json;
const currentVersion = $input.item.json.query?.currentVersion || $input.item.json.currentVersion || "1.0.0";

// 2. Prüfe auf GitHub API Fehler (404, keine Releases, etc.)
const hasError = githubResponse.message || githubResponse.error || !githubResponse.tag_name;

if (hasError) {
  // GitHub API Fehler oder kein Release gefunden
  // Gibt Default-Response zurück: latestVersion = currentVersion, available = false
  return {
    json: {
      ...$input.item.json,
      latestVersion: currentVersion,
      downloadUrl: null,
      changelog: null,
      currentVersion: currentVersion,
      githubTag: null,
      githubPublishedAt: null,
      githubError: githubResponse.message || githubResponse.error || "No release found",
      hasError: true
    }
  };
}

// 3. Extrahiere Latest Version aus GitHub Tag
// GitHub Tag Format: "v1.1.0" → "1.1.0"
let latestVersion = null;
if (githubResponse.tag_name) {
  latestVersion = githubResponse.tag_name.replace(/^v/, ""); // Remove "v" prefix
}

// 4. Finde .zip Asset für Download URL
// WICHTIG: Shopware benötigt direkt zugängliche .zip URL (GitHub Releases)
// Format: https://github.com/owner/repo/releases/download/tag/plugin-name.zip
let downloadUrl = null;
if (githubResponse.assets && Array.isArray(githubResponse.assets)) {
  // Suche nach .zip Asset (priorisiere hero-blocks-*.zip)
  const zipAsset = githubResponse.assets.find(asset => {
    const name = asset.name || "";
    return name.endsWith('.zip') && (name.includes('hero-blocks') || name.includes('HeroBlocks'));
  }) || githubResponse.assets.find(asset => 
    asset.name && asset.name.endsWith('.zip')
  );
  if (zipAsset && zipAsset.browser_download_url) {
    downloadUrl = zipAsset.browser_download_url;
  }
}

// Fallback: Generiere Download URL aus Release Tag (falls kein Asset vorhanden)
if (!downloadUrl && githubResponse.tag_name && githubResponse.html_url) {
  // GitHub Releases: /releases/tag/v1.0.0 → /releases/download/v1.0.0/hero-blocks.zip
  const tagName = githubResponse.tag_name.replace(/^v/, ""); // Remove "v" prefix
  downloadUrl = githubResponse.html_url.replace(/\/tag\//, "/download/") + "/hero-blocks-" + tagName + ".zip";
}

// 5. Extrahiere Changelog aus Release Body
const changelog = githubResponse.body || githubResponse.body_text || null;

// 6. Speichere GitHub Response Daten für Version-Vergleich
// WICHTIG: Setze hasError Flag für explizites Error-Tracking
return {
  json: {
    ...$input.item.json,
    latestVersion: latestVersion || currentVersion,
    downloadUrl: downloadUrl || null,
    changelog: changelog || null,
    currentVersion: currentVersion,
    githubTag: githubResponse.tag_name || null,
    githubPublishedAt: githubResponse.published_at || null,
    hasError: false
  }
};

// WICHTIG: Version-Vergleich für Update-Check
// Best Practices: Semantic Versioning, robust, sauber mit Error-Handling
// Harmoniert mit GitHub Releases API Response

// 1. Lese aktuelle Version aus Query-Parametern
const currentVersion = $input.item.json.query?.currentVersion || $input.item.json.currentVersion || "1.0.0";

// 2. Prüfe auf GitHub API Fehler
// WICHTIG: hasError Flag wird bereits im Process GitHub Response Node gesetzt
const hasError = $input.item.json.hasError === true || $input.item.json.githubError || !$input.item.json.githubTag;

if (hasError) {
  // GitHub API Fehler oder kein Release gefunden
  // Gibt Default-Response zurück: available = false, latestVersion = currentVersion
  return {
    json: {
      ...$input.item.json,
      available: false,
      currentVersion: currentVersion,
      latestVersion: currentVersion,
      downloadUrl: null,
      changelog: null
    }
  };
}

// 3. Lese neueste Version aus GitHub Response (bereits verarbeitet)
const latestVersion = $input.item.json.latestVersion || currentVersion;

// 4. Version-Vergleich (Semantic Versioning)
function compareVersions(v1, v2) {
  const parts1 = v1.split('.').map(Number);
  const parts2 = v2.split('.').map(Number);

  for (let i = 0; i < Math.max(parts1.length, parts2.length); i++) {
    const part1 = parts1[i] || 0;
    const part2 = parts2[i] || 0;

    if (part1 < part2) return -1;
    if (part1 > part2) return 1;
  }

  return 0;
}

// 5. Prüfe ob Update verfügbar ist
// WICHTIG: available = true nur wenn latestVersion > currentVersion
// Wenn latestVersion === currentVersion → available = false
// Wenn latestVersion < currentVersion → available = false (bereits neueste Version)
const versionComparison = compareVersions(currentVersion, latestVersion);
const updateAvailable = latestVersion && latestVersion !== currentVersion && versionComparison < 0;

// 6. Generiere Update-Informationen
// WICHTIG: downloadUrl und changelog werden bereits aus GitHub Response extrahiert
// WICHTIG: Gibt immer alle Felder zurück (auch wenn available=false), damit Shopware Admin UI korrekt angezeigt wird
return {
  json: {
    ...$input.item.json,
    available: updateAvailable,
    currentVersion: currentVersion,
    latestVersion: latestVersion,
    // downloadUrl und changelog bereits vorhanden (aus GitHub Response)
    downloadUrl: $input.item.json.downloadUrl || null,
    changelog: $input.item.json.changelog || null
  }
};
